import React, { useState, useEffect } from 'react';
import { Settings, User, Database, Activity, Shield, Plus, Edit, Trash2, TestTube, Eye, EyeOff } from 'lucide-react';
import { userApi, applicationApi } from '../../services/api';

// Types based on your controllers
interface UserSettings {
    userId: string;
    firstName: string;
    lastName: string;
    username: string;
    email: string;
    phoneNumber: string;
}

interface Application {
    applicationId: string;
    applicationName: string;
    applicationDescription: string;
    connection: {
        host: string;
        port: string;
        databaseName: string;
        authenticationType: string;
    };
}

interface AuditLog {
    auditLogId: string;
    userId: string;
    username: string;
    actionType: string;
    metadata: string;
    createdAt: string;
}

interface UserActivityLog {
    userActivityLogId: string;
    userId: string;
    actionType: string;
    timestamp: string;
    user: {
        username: string;
    };
}

const SettingsDashboard: React.FC = () => {
    const [activeTab, setActiveTab] = useState('user');
    const [userSettings, setUserSettings] = useState<UserSettings | null>(null);
    const [applications, setApplications] = useState<Application[]>([]);
    const [auditLogs, setAuditLogs] = useState<AuditLog[]>([]);
    const [userActivityLogs, setUserActivityLogs] = useState<UserActivityLog[]>([]);
    const [loading, setLoading] = useState(false);
    const [showPasswordForm, setShowPasswordForm] = useState(false);
    const [showApplicationForm, setShowApplicationForm] = useState(false);
    const [editingApp, setEditingApp] = useState<Application | null>(null);

    // Password change form state
    const [passwordForm, setPasswordForm] = useState({
        currentPassword: '',
        newPassword: '',
        reTypeNewPassword: ''
    });

    // Application form state
    const [applicationForm, setApplicationForm] = useState({
        applicationName: '',
        applicationDescription: '',
        dataSourceType: 'MicrosoftSqlServer',
        connectionSource: {
            host: '',
            port: '1433',
            databaseName: '',
            authenticationType: 'UserPassword',
            username: '',
            password: ''
        }
    });

    const [showPasswords, setShowPasswords] = useState({
        current: false,
        new: false,
        reType: false,
        connection: false
    });

    const fetchUserSettings = async () => {
        setLoading(true);
        try {
            const response = await userApi.getProfile();
            if (response.success && response.data?.user) {
                setUserSettings(response.data.user);
            }
        } catch (error) {
            console.error('Failed to fetch user settings:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchApplications = async () => {
        setLoading(true);
        try {
            const response = await applicationApi.getApplications();
            if (response.success && response.data?.data) {
                setApplications(response.data.data);
            }
        } catch (error) {
            console.error('Failed to fetch applications:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchAuditLogs = async () => {
        setLoading(true);
        try {
            const response = await fetch('/AuditLogSettings');
            const data = await response.json();
            setAuditLogs(data);
        } catch (error) {
            console.error('Failed to fetch audit logs:', error);
        } finally {
            setLoading(false);
        }
    };

    const fetchUserActivityLogs = async () => {
        setLoading(true);
        try {
            const response = await fetch('/UserActivitySettings');
            const data = await response.json();
            setUserActivityLogs(data);
        } catch (error) {
            console.error('Failed to fetch user activity logs:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchUserSettings();
        fetchApplications();
        fetchAuditLogs();
        fetchUserActivityLogs();
    }, []);

    const handlePasswordChange = async () => {
        try {
            const response = await userApi.changePassword(passwordForm);
            if (response.success) {
                alert('Password changed successfully!');
                setPasswordForm({ currentPassword: '', newPassword: '', reTypeNewPassword: '' });
                setShowPasswordForm(false);
            } else {
                alert(response.message || 'Failed to change password');
            }
        } catch (error: any) {
            console.error('Failed to change password:', error);
            alert(error.message || 'Failed to change password');
        }
    };

    const handleApplicationSubmit = async () => {
        try {
            let response;
            if (editingApp) {
                response = await applicationApi.updateApplication(editingApp.applicationId, applicationForm);
            } else {
                response = await applicationApi.createApplication(applicationForm);
            }

            if (response.success) {
                alert(editingApp ? 'Application updated successfully!' : 'Application created successfully!');
                setShowApplicationForm(false);
                setEditingApp(null);
                fetchApplications();
            } else {
                alert(response.message || 'Failed to save application');
            }
        } catch (error: any) {
            console.error('Failed to save application:', error);
            alert(error.message || 'Failed to save application');
        }
    };

    const testConnection = async (applicationId?: string) => {
        try {
            const response = await applicationApi.testConnection(
                applicationId ? { applicationId } : applicationForm
            );

            if (response.success) {
                alert('Connection test successful!');
            } else {
                alert(response.message || 'Connection test failed');
            }
        } catch (error: any) {
            console.error('Connection test failed:', error);
            alert(error.message || 'Connection test failed');
        }
    };

    const deleteApplication = async (applicationId: string) => {
        if (!window.confirm('Are you sure you want to delete this application?')) return;

        try {
            const response = await applicationApi.deleteApplication(applicationId);

            if (response.success) {
                alert('Application deleted successfully!');
                fetchApplications();
            } else {
                alert(response.message || 'Failed to delete application');
            }
        } catch (error: any) {
            console.error('Failed to delete application:', error);
            alert(error.message || 'Failed to delete application');
        }
    };

    const tabs = [
        { id: 'user', label: 'User Settings', icon: User },
        { id: 'applications', label: 'Applications', icon: Database },
        { id: 'audit', label: 'Audit Logs', icon: Shield },
        { id: 'activity', label: 'Activity Logs', icon: Activity }
    ];

    const togglePasswordVisibility = (field: keyof typeof showPasswords) => {
        setShowPasswords(prev => ({ ...prev, [field]: !prev[field] }));
    };

    return (
        <div className="min-h-screen bg-gray-50">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                {/* Header */}
                <div className="mb-8">
                    <div className="flex items-center space-x-3">
                        <Settings className="h-8 w-8 text-blue-600" />
                        <h1 className="text-3xl font-bold text-gray-900">Settings</h1>
                    </div>
                    <p className="mt-2 text-gray-600">Manage your account, applications, and view system logs</p>
                </div>

                {/* Tab Navigation */}
                <div className="border-b border-gray-200 mb-8">
                    <nav className="-mb-px flex space-x-8">
                        {tabs.map((tab) => {
                            const Icon = tab.icon;
                            return (
                                <button
                                    key={tab.id}
                                    onClick={() => setActiveTab(tab.id)}
                                    className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm ${
                                        activeTab === tab.id
                                            ? 'border-blue-500 text-blue-600'
                                            : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                                    }`}
                                >
                                    <Icon className="h-5 w-5" />
                                    <span>{tab.label}</span>
                                </button>
                            );
                        })}
                    </nav>
                </div>

                {/* Tab Content */}
                {activeTab === 'user' && (
                    <div className="space-y-6">
                        <div className="bg-white shadow rounded-lg p-6">
                            <h2 className="text-xl font-semibold text-gray-900 mb-4">User Information</h2>
                            {userSettings ? (
                                <div className="grid grid-cols-2 gap-4">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">First Name</label>
                                        <p className="mt-1 text-sm text-gray-900">{userSettings.firstName}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Last Name</label>
                                        <p className="mt-1 text-sm text-gray-900">{userSettings.lastName}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Username</label>
                                        <p className="mt-1 text-sm text-gray-900">{userSettings.username}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Email</label>
                                        <p className="mt-1 text-sm text-gray-900">{userSettings.email}</p>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Phone Number</label>
                                        <p className="mt-1 text-sm text-gray-900">{userSettings.phoneNumber}</p>
                                    </div>
                                </div>
                            ) : (
                                <div className="animate-pulse">Loading user information...</div>
                            )}
                        </div>

                        <div className="bg-white shadow rounded-lg p-6">
                            <div className="flex justify-between items-center mb-4">
                                <h2 className="text-xl font-semibold text-gray-900">Password</h2>
                                <button
                                    onClick={() => setShowPasswordForm(!showPasswordForm)}
                                    className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
                                >
                                    Change Password
                                </button>
                            </div>

                            {showPasswordForm && (
                                <div className="space-y-4">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Current Password</label>
                                        <div className="relative">
                                            <input
                                                type={showPasswords.current ? "text" : "password"}
                                                value={passwordForm.currentPassword}
                                                onChange={(e) => setPasswordForm({...passwordForm, currentPassword: e.target.value})}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 pr-10"
                                            />
                                            <button
                                                type="button"
                                                onClick={() => togglePasswordVisibility('current')}
                                                className="absolute right-2 top-1/2 transform -translate-y-1/2"
                                            >
                                                {showPasswords.current ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                            </button>
                                        </div>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">New Password</label>
                                        <div className="relative">
                                            <input
                                                type={showPasswords.new ? "text" : "password"}
                                                value={passwordForm.newPassword}
                                                onChange={(e) => setPasswordForm({...passwordForm, newPassword: e.target.value})}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 pr-10"
                                            />
                                            <button
                                                type="button"
                                                onClick={() => togglePasswordVisibility('new')}
                                                className="absolute right-2 top-1/2 transform -translate-y-1/2"
                                            >
                                                {showPasswords.new ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                            </button>
                                        </div>
                                    </div>
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700">Confirm New Password</label>
                                        <div className="relative">
                                            <input
                                                type={showPasswords.reType ? "text" : "password"}
                                                value={passwordForm.reTypeNewPassword}
                                                onChange={(e) => setPasswordForm({...passwordForm, reTypeNewPassword: e.target.value})}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 pr-10"
                                            />
                                            <button
                                                type="button"
                                                onClick={() => togglePasswordVisibility('reType')}
                                                className="absolute right-2 top-1/2 transform -translate-y-1/2"
                                            >
                                                {showPasswords.reType ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                            </button>
                                        </div>
                                    </div>
                                    <div className="flex space-x-3">
                                        <button
                                            onClick={handlePasswordChange}
                                            className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
                                        >
                                            Update Password
                                        </button>
                                        <button
                                            onClick={() => setShowPasswordForm(false)}
                                            className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400"
                                        >
                                            Cancel
                                        </button>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                )}

                {activeTab === 'applications' && (
                    <div className="space-y-6">
                        <div className="bg-white shadow rounded-lg p-6">
                            <div className="flex justify-between items-center mb-4">
                                <h2 className="text-xl font-semibold text-gray-900">Database Applications</h2>
                                <button
                                    onClick={() => setShowApplicationForm(true)}
                                    className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 flex items-center space-x-2"
                                >
                                    <Plus className="h-4 w-4" />
                                    <span>Add Application</span>
                                </button>
                            </div>

                            <div className="space-y-4">
                                {applications.map((app) => (
                                    <div key={app.applicationId} className="border border-gray-200 rounded-lg p-4">
                                        <div className="flex justify-between items-start">
                                            <div>
                                                <h3 className="font-medium text-gray-900">{app.applicationName}</h3>
                                                <p className="text-sm text-gray-600">{app.applicationDescription}</p>
                                                <p className="text-sm text-gray-500 mt-1">
                                                    {app.connection.host}:{app.connection.port}/{app.connection.databaseName}
                                                </p>
                                            </div>
                                            <div className="flex space-x-2">
                                                <button
                                                    onClick={() => testConnection(app.applicationId)}
                                                    className="text-blue-600 hover:text-blue-800"
                                                >
                                                    <TestTube className="h-4 w-4" />
                                                </button>
                                                <button
                                                    onClick={() => {
                                                        setEditingApp(app);
                                                        setShowApplicationForm(true);
                                                    }}
                                                    className="text-gray-600 hover:text-gray-800"
                                                >
                                                    <Edit className="h-4 w-4" />
                                                </button>
                                                <button
                                                    onClick={() => deleteApplication(app.applicationId)}
                                                    className="text-red-600 hover:text-red-800"
                                                >
                                                    <Trash2 className="h-4 w-4" />
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>

                        {showApplicationForm && (
                            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                                <div className="bg-white rounded-lg p-6 w-full max-w-md max-h-screen overflow-y-auto">
                                    <h3 className="text-lg font-semibold mb-4">
                                        {editingApp ? 'Edit Application' : 'Add New Application'}
                                    </h3>
                                    <div className="space-y-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700">Application Name</label>
                                            <input
                                                type="text"
                                                value={applicationForm.applicationName}
                                                onChange={(e) => setApplicationForm({...applicationForm, applicationName: e.target.value})}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700">Description</label>
                                            <textarea
                                                value={applicationForm.applicationDescription}
                                                onChange={(e) => setApplicationForm({...applicationForm, applicationDescription: e.target.value})}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                                rows={3}
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700">Host</label>
                                            <input
                                                type="text"
                                                value={applicationForm.connectionSource.host}
                                                onChange={(e) => setApplicationForm({
                                                    ...applicationForm,
                                                    connectionSource: {...applicationForm.connectionSource, host: e.target.value}
                                                })}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700">Port</label>
                                            <input
                                                type="text"
                                                value={applicationForm.connectionSource.port}
                                                onChange={(e) => setApplicationForm({
                                                    ...applicationForm,
                                                    connectionSource: {...applicationForm.connectionSource, port: e.target.value}
                                                })}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700">Database Name</label>
                                            <input
                                                type="text"
                                                value={applicationForm.connectionSource.databaseName}
                                                onChange={(e) => setApplicationForm({
                                                    ...applicationForm,
                                                    connectionSource: {...applicationForm.connectionSource, databaseName: e.target.value}
                                                })}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700">Username</label>
                                            <input
                                                type="text"
                                                value={applicationForm.connectionSource.username}
                                                onChange={(e) => setApplicationForm({
                                                    ...applicationForm,
                                                    connectionSource: {...applicationForm.connectionSource, username: e.target.value}
                                                })}
                                                className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700">Password</label>
                                            <div className="relative">
                                                <input
                                                    type={showPasswords.connection ? "text" : "password"}
                                                    value={applicationForm.connectionSource.password}
                                                    onChange={(e) => setApplicationForm({
                                                        ...applicationForm,
                                                        connectionSource: {...applicationForm.connectionSource, password: e.target.value}
                                                    })}
                                                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 pr-10"
                                                />
                                                <button
                                                    type="button"
                                                    onClick={() => togglePasswordVisibility('connection')}
                                                    className="absolute right-2 top-1/2 transform -translate-y-1/2"
                                                >
                                                    {showPasswords.connection ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                                </button>
                                            </div>
                                        </div>
                                        <div className="flex space-x-3 pt-4">
                                            <button
                                                onClick={() => testConnection()}
                                                className="bg-yellow-600 text-white px-4 py-2 rounded-md hover:bg-yellow-700 flex items-center space-x-2"
                                            >
                                                <TestTube className="h-4 w-4" />
                                                <span>Test Connection</span>
                                            </button>
                                            <button
                                                onClick={handleApplicationSubmit}
                                                className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
                                            >
                                                {editingApp ? 'Update' : 'Create'}
                                            </button>
                                            <button
                                                onClick={() => {
                                                    setShowApplicationForm(false);
                                                    setEditingApp(null);
                                                }}
                                                className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400"
                                            >
                                                Cancel
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                )}

                {activeTab === 'audit' && (
                    <div className="bg-white shadow rounded-lg p-6">
                        <h2 className="text-xl font-semibold text-gray-900 mb-4">Audit Logs</h2>
                        <div className="overflow-x-auto">
                            <table className="min-w-full divide-y divide-gray-200">
                                <thead className="bg-gray-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Metadata</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                                </tr>
                                </thead>
                                <tbody className="bg-white divide-y divide-gray-200">
                                {auditLogs.map((log) => (
                                    <tr key={log.auditLogId}>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{log.username}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{log.actionType}</td>
                                        <td className="px-6 py-4 text-sm text-gray-900">{log.metadata}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                            {new Date(log.createdAt).toLocaleString()}
                                        </td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                )}

                {activeTab === 'activity' && (
                    <div className="bg-white shadow rounded-lg p-6">
                        <h2 className="text-xl font-semibold text-gray-900 mb-4">User Activity Logs</h2>
                        <div className="overflow-x-auto">
                            <table className="min-w-full divide-y divide-gray-200">
                                <thead className="bg-gray-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Timestamp</th>
                                </tr>
                                </thead>
                                <tbody className="bg-white divide-y divide-gray-200">
                                {userActivityLogs.map((log) => (
                                    <tr key={log.userActivityLogId}>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{log.user.username}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{log.actionType}</td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                            {new Date(log.timestamp).toLocaleString()}
                                        </td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SettingsDashboard;
