import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useForm, validationRules } from '../../hooks/useForm';
import { authApi } from '../../services/api';
import { Eye, EyeOff, Lock, User, AlertCircle, CheckCircle } from 'lucide-react';

interface LoginFormData {
  username: string;
  password: string;
}

interface RecoveryFormData {
  recoveryEmail: string;
  recoveryType: string;
}

const LoginForm: React.FC = () => {
  const [showRecovery, setShowRecovery] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [recoverySuccess, setRecoverySuccess] = useState(false);
  const { login, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const from = (location.state as any)?.from?.pathname || '/dashboard';

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate(from, { replace: true });
    }
  }, [isAuthenticated, navigate, from]);

  const loginForm = useForm<LoginFormData>({
    initialValues: {
      username: '',
      password: '',
    },
    validationRules: {
      username: validationRules.username,
      password: { required: true },
    },
    onSubmit: async (values) => {
      const result = await login(values);
      if (result.success) {
        navigate(from, { replace: true });
      } else {
        loginForm.setError('password', result.message);
      }
    },
  });

  const recoveryForm = useForm<RecoveryFormData>({
    initialValues: {
      recoveryEmail: '',
      recoveryType: 'PASSWORD',
    },
    validationRules: {
      recoveryEmail: validationRules.email,
      recoveryType: validationRules.required,
    },
    onSubmit: async (values) => {
      try {
        await authApi.forgotPassword(values.recoveryEmail, values.recoveryType);
        setRecoverySuccess(true);
        recoveryForm.reset();
        setTimeout(() => {
          setShowRecovery(false);
          setRecoverySuccess(false);
        }, 3000);
      } catch (error: any) {
        recoveryForm.setError('recoveryEmail', error.message || 'Recovery request failed');
      }
    },
  });

  const handleCreateAccount = () => {
    navigate('/sign-up');
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Sign in to your account
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Or{' '}
            <button
              type="button"
              onClick={handleCreateAccount}
              className="font-medium text-blue-600 hover:text-blue-500"
            >
              create a new account
            </button>
          </p>
        </div>

        <form className="mt-8 space-y-6" onSubmit={loginForm.handleSubmit}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="username" className="sr-only">
                Username
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <User className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  id="username"
                  name="username"
                  type="text"
                  value={loginForm.values.username}
                  onChange={(e) => loginForm.setValue('username', e.target.value)}
                  className={`appearance-none rounded-none relative block w-full px-12 py-3 border ${
                    loginForm.errors.username && loginForm.touched.username
                      ? 'border-red-300 text-red-900 placeholder-red-300'
                      : 'border-gray-300 placeholder-gray-500 text-gray-900'
                  } rounded-t-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm`}
                  placeholder="Username"
                />
              </div>
              {loginForm.errors.username && loginForm.touched.username && (
                <div className="mt-1 flex items-center text-sm text-red-600">
                  <AlertCircle className="h-4 w-4 mr-1" />
                  {loginForm.errors.username}
                </div>
              )}
            </div>

            <div>
              <label htmlFor="password" className="sr-only">
                Password
              </label>
              <div className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Lock className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  value={loginForm.values.password}
                  onChange={(e) => loginForm.setValue('password', e.target.value)}
                  className={`appearance-none rounded-none relative block w-full px-12 py-3 border ${
                    loginForm.errors.password && loginForm.touched.password
                      ? 'border-red-300 text-red-900 placeholder-red-300'
                      : 'border-gray-300 placeholder-gray-500 text-gray-900'
                  } rounded-b-md focus:outline-none focus:ring-blue-500 focus:border-blue-500 focus:z-10 sm:text-sm`}
                  placeholder="Password"
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                >
                  {showPassword ? (
                    <EyeOff className="h-5 w-5 text-gray-400" />
                  ) : (
                    <Eye className="h-5 w-5 text-gray-400" />
                  )}
                </button>
              </div>
              {loginForm.errors.password && loginForm.touched.password && (
                <div className="mt-1 flex items-center text-sm text-red-600">
                  <AlertCircle className="h-4 w-4 mr-1" />
                  {loginForm.errors.password}
                </div>
              )}
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loginForm.isSubmitting || !loginForm.isValid}
              className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loginForm.isSubmitting ? (
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white" />
              ) : (
                'Sign in'
              )}
            </button>
          </div>

          <div className="text-center">
            <button
              type="button"
              onClick={() => setShowRecovery(!showRecovery)}
              className="text-sm text-blue-600 hover:text-blue-500"
            >
              {showRecovery ? 'Cancel' : 'Forgot your password?'}
            </button>
          </div>
        </form>

        {showRecovery && (
          <div className="mt-8 bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Password Recovery</h3>

            {recoverySuccess ? (
              <div className="flex items-center p-4 bg-green-50 rounded-md">
                <CheckCircle className="h-5 w-5 text-green-400 mr-2" />
                <span className="text-sm text-green-800">
                  Recovery email sent successfully! Check your inbox.
                </span>
              </div>
            ) : (
              <form onSubmit={recoveryForm.handleSubmit} className="space-y-4">
                <div>
                  <label htmlFor="recoveryEmail" className="block text-sm font-medium text-gray-700">
                    Email Address
                  </label>
                  <input
                    id="recoveryEmail"
                    name="recoveryEmail"
                    type="email"
                    value={recoveryForm.values.recoveryEmail}
                    onChange={(e) => recoveryForm.setValue('recoveryEmail', e.target.value)}
                    className={`mt-1 block w-full px-3 py-2 border ${
                      recoveryForm.errors.recoveryEmail && recoveryForm.touched.recoveryEmail
                        ? 'border-red-300'
                        : 'border-gray-300'
                    } rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm`}
                    placeholder="Enter your email"
                  />
                  {recoveryForm.errors.recoveryEmail && recoveryForm.touched.recoveryEmail && (
                    <div className="mt-1 flex items-center text-sm text-red-600">
                      <AlertCircle className="h-4 w-4 mr-1" />
                      {recoveryForm.errors.recoveryEmail}
                    </div>
                  )}
                </div>

                <div>
                  <label htmlFor="recoveryType" className="block text-sm font-medium text-gray-700">
                    Recovery Type
                  </label>
                  <select
                    id="recoveryType"
                    name="recoveryType"
                    value={recoveryForm.values.recoveryType}
                    onChange={(e) => recoveryForm.setValue('recoveryType', e.target.value)}
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                  >
                    <option value="PASSWORD">Password</option>
                    <option value="USERNAME">Username</option>
                  </select>
                </div>

                <div>
                  <button
                    type="submit"
                    disabled={recoveryForm.isSubmitting || !recoveryForm.isValid}
                    className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-yellow-600 hover:bg-yellow-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500 disabled:opacity-50"
                  >
                    {recoveryForm.isSubmitting ? (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white" />
                    ) : (
                      'Send Recovery Email'
                    )}
                  </button>
                </div>
              </form>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default LoginForm;
