import React from 'react';
import { NavLink as RouterLink, useLocation } from 'react-router-dom';
import { Home, Users, Settings } from 'lucide-react';
import './NavMenu.css';

interface NavItem {
  path: string;
  label: string;
  icon: React.ComponentType<any>;
}

const navItems: NavItem[] = [
  { path: '/dashboard', label: 'Dashboard', icon: Home },
  { path: '/accounts', label: 'Accounts', icon: Users },
  { path: '/settings', label: 'Settings', icon: Settings },
];

export default function NavMenu() {
  const location = useLocation();

  return (
    <nav className="bg-light border-end d-flex flex-column" style={{ width: '220px', minHeight: '100%' }}>
      <div className="p-3">
        <h6 className="text-muted text-uppercase small fw-bold mb-3">Navigation</h6>
        <ul className="nav flex-column">
          {navItems.map((item) => {
            const Icon = item.icon;
            const isActive = location.pathname === item.path;

            return (
              <li key={item.path} className="nav-item mb-1">
                <RouterLink
                  to={item.path}
                  className={`nav-link d-flex align-items-center px-3 py-2 rounded ${
                    isActive 
                      ? 'bg-primary text-white' 
                      : 'text-dark hover-bg-light'
                  }`}
                  style={{ textDecoration: 'none' }}
                >
                  <Icon size={18} className="me-2" />
                  <span>{item.label}</span>
                </RouterLink>
              </li>
            );
          })}
        </ul>
      </div>

      {/* Footer or additional navigation sections can go here */}
      <div className="mt-auto p-3 border-top">
        <small className="text-muted">
          CAMS v1.0
        </small>
      </div>
    </nav>
  );
}
